//get sys_id of toggle variable
var toggleSysId = '';
var toggleVar = new GlideRecord('item_option_new');
toggleVar.addQuery('cat_item', data.get(8).cat_sys_id);
toggleVar.addQuery('name', 'adv_toggle');
toggleVar.query();
if(toggleVar.next()){
toggleSysId = toggleVar.sys_id;
}


//associate toggle var with client script
var clientScript = new GlideRecord('catalog_script_client');
clientScript.addQuery('cat_item', data.get(8).cat_sys_id);
clientScript.addQuery('name', 'display toggle');
clientScript.query();
while(clientScript.next()){
clientScript.cat_variable="IO:"+toggleSysId;
clientScript.update();
}

